import{_ as e,c as i,o as t,a}from"./app.b813a189.js";const u=JSON.parse('{"title":"SVG\u52A8\u753B","description":"","frontmatter":{"title":"SVG\u52A8\u753B"},"headers":[],"relativePath":"svg/animation/a-guide-to-svg-animations.md","lastUpdated":1668506830000}'),o={name:"svg/animation/a-guide-to-svg-animations.md"},l=a('<p>\u63A5\u4E0B\u6765\u76845\u5C0F\u8282\u90FD\u662F <a href="https://css-tricks.com/guide-svg-animations-smil/" target="_blank" rel="noreferrer">Guide SVG animations SMIL - Sara Soueidan@CSSTricks</a> \u4E0A\u5173\u4E8E <code>SVG SMIL</code> \u52A8\u753B\u6587\u7AE0\u7684\u62C6\u5206\uFF0C\u539F\u6587\u7AE0\u5B9E\u5728\u662F\u592A\u957F\u4E86\uFF0C\u5982\u679C\u653E\u4E00\u8D77\u4F1A\u663E\u5F97\u5F88\u6050\u6016\u3002\u4E8E\u662F\u6211\u6309\u7167\u5927\u81F4\u4E3B\u9898\u5BF9\u6587\u7AE0\u8FDB\u884C\u4E86\u62C6\u5206\uFF1A</p><ol><li><a href="./svg-animation-overview.html">1-\u6982\u89C8</a> \u6574\u4F53\u4ECB\u7ECD\u4E86SMIL\u52A8\u753B\u5143\u7D20\u4EE5\u53CASVG\u52A8\u753B\u7684\u4F18\u52BF <ul><li>\u52A8\u753B\u5143\u7D20 <code>&lt;animate&gt;</code> &amp; <code>&lt;set&gt;</code> &amp; <code>&lt;animateMotion&gt;</code> &amp; <code>&lt;animateTransform&gt;</code></li></ul></li><li><a href="./svg-animation-attributes.html">2-\u52A8\u753B\u5143\u7D20\u7279\u6027\u4ECB\u7ECD</a> \u6838\u5FC3\u90E8\u5206\uFF0C\u4ECB\u7ECD\u4E86\u5927\u91CF\u52A8\u753B\u6240\u9700\u7684\u7279\u6027 <ul><li><code>xlink:href</code> - \u5E94\u7528\u52A8\u753B\u76EE\u6807\u6216\u8005\u8DEF\u5F84</li><li><code>attributeName</code> - \u8981\u52A8\u753B\u7684\u5C5E\u6027\u540D</li><li><code>from, by, to, begin, dur, fill</code> - \u6838\u5FC3\u5C5E\u6027</li><li>\u5982\u4F55\u5BF9\u591A\u4E2A\u52A8\u753B\u6548\u679C\u8FDB\u884C\u7F16\u6392\uFF08\u7C7B\u4F3CGSAP timelines\uFF09</li><li><code>repeatCount, repeatDur</code> - \u5173\u4E8E\u91CD\u590D\u52A8\u753B\uFF0C\u4EE5\u53CA\u91CD\u590D\u52A8\u753B\u7F16\u6392</li><li><code>keyTimes &amp; values</code> - \u5B9E\u73B0\u7C7B\u4F3CCSS\u4E2D\u7C7B\u4F3C <code>@keyframes</code> \u6548\u679C</li><li><code>calcMode + keySplines</code> - \u5B9A\u4E49 <code>@keyframes</code> \u6BCF\u4E2A\u65F6\u95F4\u6BB5\u4E4B\u95F4\u7684\u7F13\u52A8\u51FD\u6570</li><li><code>additive, accumulate</code> - \u5BF9\u4E8E\u91CD\u590D\u52A8\u753B\uFF0C\u52A8\u753B\u53E0\u52A0\u6548\u679C</li><li><code>end</code> - \u6307\u5B9A\u52A8\u753B\u7ED3\u675F\u6761\u4EF6</li><li><code>\u591A\u7EC4 begin + end</code> - \u5B9A\u4E49\u95F4\u6B47\u6027\u52A8\u753B\u6548\u679C</li><li><code>min, max</code> - \u5B9A\u4E49\u52A8\u753B\u6267\u884C\u7684\u65F6\u957F</li></ul></li><li><a href="./svg-animation-morphing-path.html">3-Morphing Path</a> \u5BF9 <code>path | points</code> \u5F62\u72B6 \u8F6C\u6362\u4E3A\u53E6\u4E00\u4E2A\u5F62\u72B6\u7684\u52A8\u753B\u6548\u679C <ul><li>SMIL\u5BF9\u4E8Emorphing\u6548\u679C\u5B58\u5728\u4E00\u4E9B\u73B0\u5728\uFF0C\u9876\u70B9\u6570\u91CF\u5FC5\u987B\u4FDD\u6301\u4E00\u81F4</li><li>\u989D\u5916\u8865\u5145\u4E86\u5F88\u591A\u5B9E\u73B0SVG Morphing Path\u6548\u679C\u7684\u5E93\u4EE5\u53CA\u8D44\u6E90\uFF08\u8BD1\u8005\u6DFB\u52A0\uFF09</li></ul></li><li><a href="./svg-animation-motion-path.html">4-\u6CBF\u8DEF\u5F84\u52A8\u753B</a> \u5F88\u5E38\u89C1\u7684\u4E00\u79CD\u52A8\u753B\u5F62\u5F0F\uFF0C\u5982\u4F55\u6CBF\u7740\u6307\u5B9A\u8DEF\u5F84\u8FDB\u884C\u52A8\u753B <ul><li>\u4F7F\u7528 <code>animateMotion\u5143\u7D20 + path\u7279\u6027</code> \u6216\u8005 <code>animateMotion\u5143\u7D20 + mpath\u5143\u7D20\u5F15\u7528\u5916\u90E8\u8DEF\u5F84</code> \u8FDB\u884C\u8DEF\u5F84\u52A8\u753B\uFF1B\u8BF7\u6CE8\u610F\u4F7F\u7528\u5B83\u4EEC\u662F\u5750\u6807\u7CFB\u7684\u5DEE\u5F02</li><li>\u4F7F\u7528 <code>rotate</code> \u7279\u6027\uFF0C\u4FDD\u8BC1\u7269\u4F53\u59CB\u7EC8\u6CBF\u7740\u8DEF\u5F84\u7684\u6B63\u5207\u65B9\u5411\u8FDB\u884C\u52A8\u753B</li><li><code>keyPoints</code> \u63A7\u5236\u8DEF\u5F84\u52A8\u753B\u8DDD\u79BB</li><li><code>textPath</code> \u4F7F\u6587\u672C\u6CBF\u7740\u8DEF\u5F84\u6446\u653E\uFF0C<code>animate + attribute=&quot;startOffset&quot;</code> \u8FDB\u884C\u6587\u672C\u6CBF\u7740\u8DEF\u5F84\u8FDB\u884C\u52A8\u753B</li><li>\u63A8\u8350\u4F7F\u7528 <a href="https://greensock.com/docs/v3/Plugins/MotionPathPlugin" target="_blank" rel="noreferrer">GSAP MotionPathPlugin</a> \u8FDB\u884C\u8DEF\u5F84\u52A8\u753B</li></ul></li><li><a href="./svg-animation-transformations.html">5-transform &amp; set</a><ul><li><code>&lt;animationTransform&gt;</code> \u5143\u7D20\uFF0C\u63D0\u4F9B <code>translate &amp; scale &amp; skew &amp; rotate</code> \u8F6C\u6362</li><li><code>&lt;set&gt;</code> \u5143\u7D20\uFF0C\u76F4\u63A5\u8BBE\u7F6E\u52A8\u753B\u5C5E\u6027</li></ul></li></ol><p>\u6269\u5C55\u94FE\u63A5\uFF1A</p><ol><li><a href="https://svgwg.org/specs/animations/" target="_blank" rel="noreferrer">SVG Animation Level 2 - w3.org</a></li><li><a href="http://slides.com/sarasoueidan/on-the-edge-of-svg" target="_blank" rel="noreferrer">Sara Soueidan \u5173\u4E8E SVG Slides</a></li><li><a href="https://bigbite.net/2013/11/27/introduction-svg-animation/" target="_blank" rel="noreferrer">An introduction to SVG animation - @bigbite</a></li></ol><p>2022\u5E7411\u670815\u65E517:36:52</p>',5),n=[l];function r(c,d,s,m,g,p){return t(),i("div",null,n)}const f=e(o,[["render",r]]);export{u as __pageData,f as default};
