import{_ as t,c as e,o,a}from"./app.b813a189.js";const h=JSON.parse('{"title":"SVG\u52A8\u753B\u6982\u89C8","description":"","frontmatter":{"title":"SVG\u52A8\u753B\u6982\u89C8"},"headers":[{"level":2,"title":"1\uFE0F\u20E3 \u2B50 SVG\u52A8\u753B\u6982\u89C8","slug":"_1\uFE0F\u20E3-\u2B50-svg\u52A8\u753B\u6982\u89C8","link":"#_1\uFE0F\u20E3-\u2B50-svg\u52A8\u753B\u6982\u89C8","children":[]},{"level":2,"title":"2\uFE0F\u20E3 \u4E3A\u4EC0\u4E48\u4F7F\u7528SVG\u52A8\u753B\uFF1F","slug":"_2\uFE0F\u20E3-\u4E3A\u4EC0\u4E48\u4F7F\u7528svg\u52A8\u753B","link":"#_2\uFE0F\u20E3-\u4E3A\u4EC0\u4E48\u4F7F\u7528svg\u52A8\u753B","children":[]},{"level":2,"title":"3\uFE0F\u20E3 \u6D4F\u89C8\u5668\u517C\u5BB9\u6027","slug":"_3\uFE0F\u20E3-\u6D4F\u89C8\u5668\u517C\u5BB9\u6027","link":"#_3\uFE0F\u20E3-\u6D4F\u89C8\u5668\u517C\u5BB9\u6027","children":[]}],"relativePath":"svg/animation/svg-animation-overview.md","lastUpdated":1668506830000}'),i={name:"svg/animation/svg-animation-overview.md"},n=a('<p>\u8FD9\u662F <a href="https://css-tricks.com/guide-svg-animations-smil/#aa-overview" target="_blank" rel="noreferrer">A Guide to SVG Animations (SMIL)</a> \u7684\u90E8\u5206\u5185\u5BB9\u3002</p><h2 id="_1\uFE0F\u20E3-\u2B50-svg\u52A8\u753B\u6982\u89C8" tabindex="-1">1\uFE0F\u20E3 \u2B50 SVG\u52A8\u753B\u6982\u89C8 <a class="header-anchor" href="#_1\uFE0F\u20E3-\u2B50-svg\u52A8\u753B\u6982\u89C8" aria-hidden="true">#</a></h2><p>SVG\u56FE\u5F62\u53EF\u4EE5\u4F7F\u7528 <strong>\u52A8\u753B\u5143\u7D20\uFF08<code>animation elements</code>\uFF09</strong> \u8FDB\u884C\u52A8\u753B\u3002\u52A8\u753B\u5143\u7D20\u4E00\u5F00\u59CB\u5B9A\u4E49\u5728 <a href="http://www.w3.org/TR/2001/REC-smil-animation-20010904/" target="_blank" rel="noreferrer">SMIL</a> \u52A8\u753B\u89C4\u8303\u4E2D\uFF0C\u5305\u542B\u4EE5\u4E0B\u5143\u7D20\uFF1A</p><ol><li><code>&lt;animate&gt;&lt;/animate&gt;</code> - \u5141\u8BB8\u4F60\u5728\u4E00\u6BB5\u65F6\u95F4\u5185\u5BF9\u6807\u91CF\u7279\u6027\uFF08<code>attributes</code>\uFF09\u548C\u5C5E\u6027\uFF08<code>properties</code>\uFF09\u8FDB\u884C\u52A8\u753B</li><li><code>&lt;set&gt;&lt;/set&gt;</code> - <code>animate</code> \u7684\u7B80\u5199\u65B9\u5F0F\uFF0C\u5BF9\u975E\u6570\u5B57\u7C7B\u578B\u7684\u7279\u6027\u548C\u5C5E\u6027\u6DFB\u52A0\u52A8\u753B\u503C\u6709\u7528\uFF0C\u6BD4\u5982 <code>visibility</code> \u5C5E\u6027</li><li><code>&lt;animateMotion&gt;&lt;/animateMotion&gt;</code> - \u7528\u4E8E\u6CBF\u7740\u8FD0\u52A8\u8DEF\u5F84\uFF08<code>motion path</code>\uFF09\u79FB\u52A8\u5143\u7D20\u26A1\uFF0C\u8DEF\u5F84\u52A8\u753B\u7684\u5173\u952E</li><li><code>&lt;animateColor&gt;&lt;/animateColor&gt;</code> - \u5B83\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\u4FEE\u6539\u7279\u5B9A\u7279\u6027\u6216\u5C5E\u6027\u7684\u989C\u8272\u503C\uFF0C\u5DF2\u5E9F\u5F03\u{1F6A8}\uFF0C\u4F7F\u7528 <code>&lt;animate&gt;</code> \u5143\u7D20\u66FF\u4EE3</li></ol><p>\u9664\u4E86SMIL\u89C4\u8303\u4E2D\u7684\u52A8\u753B\u5143\u7D20\u5916\uFF0CSVG\u8FD8\u63D0\u4F9B\u4E86\u517C\u5BB9SMIL\u89C4\u8303\u7684\u6269\u5C55\uFF1B\u8FD9\u4E9B\u6269\u5C55\u5305\u62EC\u6269\u5C55\u4E86\u5143\u7D20\u529F\u80FD\u7684\u7279\u6027\u548C\u5176\u5B83\u52A8\u753B\u5143\u7D20\u{1F60E}\uFF1A</p><ol><li><code>&lt;animateTransform&gt;&lt;/animateTransform&gt;</code> - \u5141\u8BB8\u4F60\u5BF9SVG transformation\u7279\u6027\u8FDB\u884C\u52A8\u753B\uFF0C\u6BD4\u5982 <code>transform</code> \u7279\u6027</li><li><code>path (attribute)</code> - \u5141\u8BB8\u4EFB\u4F55SVG <code>path</code> \u6570\u636E\uFF08\u5373 <code>d</code> \u7279\u6027\uFF09\u653E\u7F6E\u5230 <code>&lt;animateMotion&gt;</code> \u5143\u7D20\u7684 <code>path</code> \u7279\u6027\u4E0A\u8FDB\u884C\u52A8\u753B\uFF08SMIL\u53EA\u652F\u6301SVG\u8DEF\u5F84\u6570\u636E\u8BED\u6CD5\u7684\u5B50\u96C6\uFF09\uFF1B\u6211\u4EEC\u5C06\u4E4B\u540E\u518D\u8BB2\u5230 <code>&lt;animateMotion&gt;</code></li><li><code>&lt;mpath&gt;&lt;/mpath&gt;</code> - \u653E\u5728 <code>&lt;animateMotion&gt;</code> \u5143\u7D20\u5185\u90E8\uFF0C\u5F15\u7528\u8981\u8FD0\u52A8\u7684\u8DEF\u5F84\uFF1B<code>&lt;animateMotion&gt;&lt;mpath xlink:href=&quot;#motionPath&quot;&gt;&lt;/mpath&gt;&lt;/animateMotion&gt;</code></li><li><code>keypoints (attribute)</code> - \u5B83\u662F <code>&lt;animateMotion&gt;</code> \u7684\u4E00\u4E2A\u7279\u6027\uFF08<code>&lt;animateMotion keypoints=&quot;xxx&quot;&gt;</code>\uFF09\uFF0C \u7528\u4E8E\u5BF9\u52A8\u753B\u8DEF\u5F84\u8FD0\u52A8\u901F\u5EA6\u63D0\u4F9B\u7CBE\u786E\u7684\u63A7\u5236</li><li><code>rotate (attribute)</code> - \u5B83\u662F <code>&lt;animateMotion&gt;</code> \u7684\u4E00\u4E2A\u7279\u6027\uFF08<code>&lt;animateMotion rotate=&quot;xxx&quot;&gt;</code>\uFF09\uFF0C\u7528\u4E8E\u63A7\u5236\u76EE\u6807\u5BF9\u8C61\u662F\u5426\u81EA\u52A8\u65CB\u8F6C\uFF0C\u4EE5\u4FBF\u5176x\u8F74\u59CB\u7EC8\u6307\u5411\u4E0E\u8FD0\u52A8\u8DEF\u5F84 <code>\u6B63\u5207</code> \u7684\u76F8\u540C\u6216\u76F8\u53CD\u7684\u65B9\u5411\uFF0C\u8FBE\u5230\u60F3\u8981\u7684\u52A8\u753B\u6548\u679C\uFF1B\u5B83\u662F\u8DEF\u5F84\u52A8\u753B\u5173\u952E\u7684\u4E00\u4E2A\u5C5E\u6027\u26A1</li></ol><p>SVG\u52A8\u753B\u5728\u672C\u8D28\u4E0A\u53EF\u4EE5\u7C7B\u4F3C\u4E8ECSS\u52A8\u753B\u548Ctransitions\u3002\u521B\u5EFAkeyframes\uFF0C\u7269\u4F53\u79FB\u52A8\uFF0C\u989C\u8272\u6539\u53D8\u7B49\u7B49\u3002\u4F46\u662F\uFF0C\u5B83\u4EEC\u80FD\u505A\u4E00\u4E9BCSS\u65E0\u6CD5\u505A\u5230\u7684\u4E8B\u60C5\uFF0C\u540E\u9762\u4F1A\u8BB2\u5230\u8FD9\u4E2A\u{1F60E}\u3002</p><h2 id="_2\uFE0F\u20E3-\u4E3A\u4EC0\u4E48\u4F7F\u7528svg\u52A8\u753B" tabindex="-1">2\uFE0F\u20E3 \u4E3A\u4EC0\u4E48\u4F7F\u7528SVG\u52A8\u753B\uFF1F <a class="header-anchor" href="#_2\uFE0F\u20E3-\u4E3A\u4EC0\u4E48\u4F7F\u7528svg\u52A8\u753B" aria-hidden="true">#</a></h2><p>\u{1F4DA} SVG\u53EF\u4EE5\u4F7F\u7528CSS\u6DFB\u52A0\u6837\u5F0F\uFF0C\u4EE5\u53CA\u4F7F\u7528CSS\u52A8\u753B\uFF1A</p><ul><li><a href="http://slides.com/sarasoueidan/styling-animating-svgs-with-css--2#/" target="_blank" rel="noreferrer">Styling animating svgs with CSS - slides</a></li></ul><p>\u57FA\u672C\u4E0A\u4EFB\u4F55\u53EF\u7528\u4E8E\u666E\u901AHTML\u5143\u7D20\u7684transformation\u548Ctransitions\uFF0C\u90FD\u53EF\u4EE5\u7528\u4E8ESVG\u5143\u7D20\u4E0A\uFF1B\u4F46\u662F\uFF0C\u6709\u4E9BSVG\u5C5E\u6027\u4E0D\u80FD\u901A\u8FC7CSS\u8FDB\u884C\u52A8\u753B\uFF0C\u53EA\u80FD\u4F7F\u7528SVG\u8FDB\u884C\u52A8\u753B\u{1F60F}\uFF1A</p><ul><li>\u6BD4\u5982\uFF0CSVG\u8DEF\u5F84\uFF08<code>path</code>\uFF09\uFF0C\u4F7F\u7528\u4E00\u7EC4<code>\u6570\u636E</code>\uFF08<code>d=&quot;&quot;</code> \u7279\u6027\uFF09\u5B9A\u4E49\u8DEF\u5F84\u5F62\u72B6\u3002\u8FD9\u4E9B\u6570\u636E\u53EF\u4EE5\u88ABSMIL\u4FEE\u6539\u548C\u52A8\u753B\uFF0C\u4F46\u662F\u4E0D\u80FD\u4F7F\u7528CSS\uFF1B</li><li>\u8FD9\u662F\u56E0\u4E3ASVG\u5143\u7D20\u88AB\u4E00\u7EC4\u79F0\u4E4B\u4E3ASVG\u8868\u73B0\u7279\u6027\uFF08<code>presentation attributes</code>\uFF09 \u7684\u7279\u6027\u6240\u63CF\u8FF0\uFF1B\u67D0\u4E9B\u7279\u6027\u53EF\u4EE5\u4F7F\u7528CSS\u8FDB\u884C\u8BBE\u7F6E\uFF0C\u4FEE\u6539\u548C\u6DFB\u52A0\u52A8\u753B\u6548\u679C\uFF0C\u800C\u6709\u4E9B\u7279\u6027\u662F\u65E0\u6CD5\u4F7F\u7528CSS\u8FDB\u884C\u4FEE\u6539\u6216\u6DFB\u52A0\u52A8\u753B\u7684\u{1F605}</li></ul><p>\u{1F4DA} \u56E0\u6B64\uFF0C\u73B0\u5728\u5F88\u591A\u52A8\u6548\u662F\u65E0\u6CD5\u7B80\u5355\u4F7F\u7528CSS\u5B8C\u6210\u7684\u3002CSS\u548CSVG\u52A8\u6548\u7684\u7A7A\u9699\u53EF\u4EE5\u4F7F\u7528JS\u6216\u8005\u6D3E\u751F\u81EASMIL\u89C4\u8303\u7684\u58F0\u660E\u5F0FSVG\u52A8\u753B\u8FDB\u884C\u586B\u8865\uFF1A</p><ul><li>\u5982\u679C\u4F60\u66F4\u559C\u6B22JS\u7684\u65B9\u5F0F\uFF0C\u63A8\u8350\u4F7F\u7528 <a href="http://snapsvg.io/" target="_blank" rel="noreferrer">snap.svg</a> \u8FD9\u4E2A\u5E93\uFF0C\u5B83\u88AB\u79F0\u4E3ASVG\u7248\u672C\u7684jQuery</li><li>\u5982\u679C\u4F60\u66F4\u559C\u6B22\u58F0\u660E\u5F0F\u52A8\u753B\u7684\u65B9\u5F0F\uFF0C\u5219\u53EF\u4EE5\u4F7F\u7528\u672C\u6587\u4ECB\u7ECD\u7684\u52A8\u753B\u5143\u7D20</li></ul><p>SMIL\u65B9\u5F0F\u6BD4JS\u52A8\u753B\u7684\u4E00\u4E2A\u4F18\u52BF\u662F\uFF1AJS\u65E0\u6CD5\u5BF9\u4F7F\u7528 <code>img</code> | <code>background-image</code> \u65B9\u5F0F\u5F15\u5165\u7684SVG\u8D44\u6E90\u8FDB\u884C\u63A7\u5236\uFF0C\u800CSMIL\u52A8\u753B\u5219\u90FD\u53EF\u4EE5\uFF0C\u8FD9\u4E5F\u53EF\u4EE5\u8BA4\u4E3A\u662FSMIL\u7684\u4E00\u4E2A\u6BD4\u8F83\u5927\u7684\u4F18\u52BF\u3002\u672C\u6587\u7684\u76EE\u7684\u5C31\u662F\u8BA9\u4F60\u5F00\u59CB\u4F7F\u7528SMIL\u{1F601}\u3002</p><h2 id="_3\uFE0F\u20E3-\u6D4F\u89C8\u5668\u517C\u5BB9\u6027" tabindex="-1">3\uFE0F\u20E3 \u6D4F\u89C8\u5668\u517C\u5BB9\u6027 <a class="header-anchor" href="#_3\uFE0F\u20E3-\u6D4F\u89C8\u5668\u517C\u5BB9\u6027" aria-hidden="true">#</a></h2><p>\u51E0\u4E4E\u6CA1\u5565\u517C\u5BB9\u6027\u95EE\u9898\uFF0C\u9664\u4E86IE\u548Copera Mimi</p><ul><li><a href="https://caniuse.com/svg-smil" target="_blank" rel="noreferrer">svg smil - CANIUSE</a></li></ul><p>2022\u5E7411\u670808\u65E517:16:26</p>',19),l=[n];function d(r,c,s,S,g,m){return o(),e("div",null,l)}const _=t(i,[["render",d]]);export{h as __pageData,_ as default};
